package $string$;

public class Basic_String {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String str1= "Hey";
		String str2= "Vikas";
		String str3= "How";
		String str4= "AUTOMATION";
		String str5= "is_going?";
		String str6= "             Focus man                ";
		String str7="";
		
		
		System.out.println(str1 + str2+ str3);//Concatenation
		System.out.println(str1.concat(str2));//Concatenation
		System.out.println(str3.length());
		System.out.println(str4.toLowerCase());
		System.out.println(str5.toUpperCase());
		System.out.println(str6.trim());
		System.out.println(str7.isEmpty());
		
		
		
	


	}

}
